import './App.css';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Services from './components/Services';
import Contact from './components/Contact';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import {Route, Switch} from "react-router-dom";
import React, { useState, useEffect }  from 'react';
import Dashboard from './components/Dashboard';
import Logout from './components/Logout';
import Protectedroute from './components/ProtectedRoute';
import AddArticle from './components/AddArticle';
import Notes from './components/Notes';
// import Posts from './components/Posts';
// import articles from '../models/articles';
import Articles from './components/Articles';
import Article from './components/Article';
import EditArticle from './components/EditArticle';


function App() 
{

  // Check if User is Logged In
  const [auth, setauth] = useState(false);
  const [auth1, setauth1] = useState(true);

  const isLoggedIn = async () => 
  {
    try
    {
      const res = await fetch('/auth', 
      {
        method : "GET",
        headers : 
        {
          Accept : "application/json",
          "Content-Type" : "application/json"
        },
        credentials : "include"
      });

      if(res.status === 200)
      {
        setauth(true)
        setauth1(false)
      }

      if(res.status === 401)
      {
        setauth(false)
        setauth1(true)
      }
    }catch(error)
    {
      console.log(error)
    }
  }
  useEffect(() => {
    isLoggedIn();
  }, []);

  return (
    <>
      <Navbar auth = {auth1} />
      <Switch>
        <Route exact path = "/" component={Home}/>
        <Route exact path = "/about" component={About} />
        <Route exact path = "/service" component={Services} />
        <Route exact path = "/contact" component={Contact} />
        <Route exact path = "/notes" component={Notes} />

        <Route exact path = "/Article" component={Article} />
        <Route exact path = "/Articles" component={Articles} />
        {/* <Route exact path = "/articles" component={articles} /> */}
        <Route exact path = "/EditArticle" component = {EditArticle} />
        <Route exact path = "/AddArticle" component = {AddArticle} />
        {/* <Route exact path = "/Posts" component = {Posts}/> */}


        <Protectedroute exact path = "/login" component={Login} auth={auth1} />
        <Protectedroute exact path = "/register" component={Register} auth={auth1} />
        <Protectedroute exact path = "/dashboard" component={Dashboard} auth={auth} />
        <Protectedroute exact path = "/logout" component={Logout} auth={auth} />
      </Switch>
      <Footer/>

    </>
  );
}

export default App;
